sp_widget.script:
(function() {

	data.userIds = getRequesterIds(gs.getUserID());
	data.userIds.push(gs.getUserID());

	var ApprovalGR ;

	// ######  For Approved & Rejected of Current month #######
	ApprovalGR = new GlideRecord('sysapproval_approver');
	ApprovalGR.addQuery('approver', 'IN', data.userIds.join(','));
	ApprovalGR.addQuery('source_table', 'IN', 'x_nriws_coupa_req_coupa_invoice,x_nriws_coupa_req_coupa_requisition');
	ApprovalGR.addEncodedQuery('sys_updated_onONThis month@javascript:gs.beginningOfThisMonth()@javascript:gs.endOfThisMonth()');
	ApprovalGR.query(); 

	data.approved_count = 0;
	data.rejected_count = 0;//sys_created_onONThis month@javascript:gs.beginningOfThisMonth()@javascript:gs.endOfThisMonth()

	while(ApprovalGR.next()){
		if(ApprovalGR.getValue('state')=='approved')
		{	data.approved_count += 1;	}

		if(ApprovalGR.getValue('state')=='rejected')
		{	data.rejected_count += 1;	}

	}

	// ######  For Pending #######
	if(true){
		ApprovalGR = new GlideRecord('sysapproval_approver');
		ApprovalGR.addQuery('approver', 'IN', data.userIds.join(','));
		ApprovalGR.addQuery('source_table', 'IN', 'x_nriws_coupa_req_coupa_invoice,x_nriws_coupa_req_coupa_requisition');
		ApprovalGR.query();

		data.pending_count = 0;

		while(ApprovalGR.next()){
			if(ApprovalGR.getValue('state')=='requested')
			{	data.pending_count += 1;	}
		}
	}


	// ######  For Approved & Rejected of Last month #######
	if(true){
		ApprovalGR = new GlideRecord('sysapproval_approver');
		ApprovalGR.addQuery('approver', 'IN', data.userIds.join(','));


		ApprovalGR.addEncodedQuery('sys_updated_onONLast month@javascript:gs.beginningOfLastMonth()@javascript:gs.endOfLastMonth()');
		//ApprovalGR.addEncodedQuery('sys_updated_onONLast week@javascript:gs.beginningOfLastWeek()@javascript:gs.endOfLastWeek()');
		ApprovalGR.query(); 

		data.approved_count_month = 0;
		data.rejected_count_month = 0;

		while(ApprovalGR.next()){

			if(ApprovalGR.getValue('state')=='approved')
			{	data.approved_count_month += 1;	}

			if(ApprovalGR.getValue('state')=='rejected')
			{	data.rejected_count_month += 1;	}



		}


	}


	data.pendingApprovalLabel = '承認待ち';
	data.approvedLabel = '当月承認済み';
	data.rejectedLabel = '当月否認済み';

	//Get Requesters for delegate
	function getRequesterIds(id) {
		var today =new GlideDateTime().getDisplayValue();
		var requester_ids = [];
		var gr = new GlideRecord('sys_user_delegate');
		gr.addQuery('delegate', id);
		gr.addQuery('approvals',true);
		gr.addQuery('ends','>=',new GlideDateTime().getDisplayValue());
		gr.addQuery('starts','<=',new GlideDateTime().getDisplayValue());
		gr.query();
		while(gr.next()) {
			// if (gr.ends.getDisplayValue() >= today && gr.starts.getDisplayValue()<=today)
			// {
			// 	requester_ids.push(gr.user.toString());
			// }
			requester_ids.push(gr.user.toString());
		}
		return requester_ids;
	}
})();
