sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var fileName = 'import_'+inputs.schedule_job_number + '.csv';

  var attachUtil = new x_nriws_coupa_req.AttachmentUtils();
  var newattachmentsysid = attachUtil.attachCSVMutipleRecords('x_nriws_coupa_req_anaplan_schedule_job',inputs.record_sys_id,fileName);

  outputs.newattachmentsysid = newattachmentsysid;

})(inputs, outputs);

